import React, { useState, useEffect } from 'react';
import Chat from './components/Chat.js';

function App() {
  const [count, setCount] = useState(0);

  var iframe = document.querySelector('.chat-embeded')
  var message = iframe.contentWindow.document.querySelector('.text-fragment');

  message.onClick = () => setCount(count + 1)

  return (
    <div className="App">
      <div>You clicked {count} times</div>
      <iframe title="chat-embeded" className="chat-embeded"
        src="https://www.twitch.tv/embed/s0mcs/chat?parent=localhost"
        height="700px"
        width="350px">
      </iframe>


    </div>
  );
}

export default App;
